<?php

namespace AdminBundle\Repository;

/**
 * ClientRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ProductRepository extends \Doctrine\ORM\EntityRepository
{
    public function countByDateAndName($nbPage, $page, $begin=null, $end=null, $search=null){
        $page = (int) $page;
        $nbPage = (int) $nbPage;
        $sql="SELECT COUNT(*) FROM product p ";

        if($search && !$begin)
            $sql .= " WHERE p.name LIKE :search OR p.description LIKE :search AND isdeleted=false";
        else if($search && $begin)
            $sql .= " WHERE p.createdat BETWEEN :begin AND :end AND (p.name LIKE :search OR p.description LIKE :search) AND isdeleted=false";
        else if(!$search && $begin)
            $sql .= " WHERE p.createdat BETWEEN :begin AND :end AND isdeleted=false";
        else
            $sql .= " WHERE isdeleted=false ";

        $sql .= " ORDER BY p.createdat DESC";
        //$sql .= " LIMIT :limit,:offset";

        $state = $this->_em->getConnection()->prepare($sql);
        if($begin) {
            $begin = date_format( new \DateTime($begin), "y-m-d");
            $end = date_format( new \DateTime($end), "y-m-d");
            $state->bindValue(":begin", $begin, \PDO::PARAM_STR);
            $state->bindValue(":end", $end, \PDO::PARAM_STR);
        }
        if($search) {
            $state->bindValue(":search", $search, \PDO::PARAM_STR);
        }
        $state->execute();
        return $state->fetch(\PDO::FETCH_COLUMN);
    }

    public function getByDateAndName($limit, $offset, $begin=null, $end=null, $search=null){
        $offset = (int) $offset;
        $limit = (int) ($limit - 1) *$offset;
        $sql="SELECT * FROM product p ";

        if($search && !$begin)
            $sql .= " WHERE p.name LIKE :search OR p.description LIKE :search AND isdeleted=false";
        else if($search && $begin)
            $sql .= " WHERE p.createdat BETWEEN :begin AND :end AND (p.name LIKE :search OR p.description LIKE :search ) AND isdeleted=false";
        else if(!$search && $begin)
            $sql .= " WHERE p.createdat BETWEEN :begin AND :end AND isdeleted=false";
        else
            $sql .= " WHERE isdeleted=false ";

        $sql .= " ORDER BY p.createdat DESC";
        $sql .= " LIMIT :limit,:offset";

        $state = $this->_em->getConnection()->prepare($sql);
        if($begin){
            $begin = date_format( new \DateTime($begin), "y-m-d");
            $end = date_format( new \DateTime($end), "y-m-d");
            $state->bindValue(":begin", $begin,\PDO::PARAM_STR);
            $state->bindValue(":end", $end,\PDO::PARAM_STR);
        }
        if($search) {
            $search = '%'.$search.'%';
            $state->bindValue(":search", $search, \PDO::PARAM_STR);
        }

        $state->bindValue(":limit", $limit,\PDO::PARAM_INT);
        $state->bindValue(":offset", $offset,\PDO::PARAM_INT);
        $state->execute();
        return $state->fetchAll();
    }
}
